<?phpnamespace Bfg\Attributes\Targets;use Illuminate\Support\Collection;/** * Class ClassConstantTarget * @package Bfg\Attributes\Targets */class ClassConstantTarget extends TargetInterface{    /**     * @param  Collection  $classes     * @param  string  $attribute     * @return array     */    public function run(Collection $classes, string $attribute): array    {        return $classes->map(function (\ReflectionClass $class) use ($attribute) {            $constants = [];            foreach ($class->getReflectionConstants() as $constant) {                if ($attrs = $this->getAttributes($constant, $attribute)) {                    foreach ($attrs as $attr) {                        $constants[] = [                            'attribute' => $attr,                            'constant' => $constant,                            'ref' => $class,                        ];                    }                }            }            return $constants;        })->collapse()->toArray();    }}