<?phpnamespace Bfg\Attributes\Targets;use Illuminate\Support\Collection;/** * Class ClassTarget * @package Bfg\Attributes\Targets */class ClassTarget extends TargetInterface{    /**     * @param  Collection  $classes     * @param  string  $attribute     * @return array     */    public function run(Collection $classes, string $attribute): array    {        return $classes->map(function (\ReflectionClass $class) use ($attribute) {            if ($attrs = $this->getAttributes($class, $attribute)) {                $classes = [];                foreach ($attrs as $attr) {                    $classes[] = [                        'attribute' => $attr,                        'class' => $class,                        'ref' => $class,                    ];                }                return $classes;            }            return null;        })->filter()->collapse()->toArray();    }}