<?phpnamespace Bfg\Attributes\Targets;use Illuminate\Support\Collection;/** * Class MethodTarget * @package Bfg\Attributes\Targets */class MethodTarget extends TargetInterface{    /**     * @param  Collection  $classes     * @param  string  $attribute     * @return array     */    public function run(Collection $classes, string $attribute): array    {        return $classes->map(function (\ReflectionClass $class) use ($attribute) {            $methods = [];            foreach ($class->getMethods() as $method) {                if ($attrs = $this->getAttributes($method, $attribute)) {                    foreach ($attrs as $attr) {                        $methods[] = [                            'attribute' => $attr,                            'method' => $method,                            'ref' => $class,                        ];                    }                }            }            return $methods;        })->collapse()->toArray();    }}